function cancelButtonClicked(button){removeSelectedNumber(button.parentNode.parentNode.id);drawSelectedNumbers();checkBetAndDisplay();}
function removeSelectedNumber(number){var index=selectedNumbers.length-1;var found=false;while(!found){if(selectedNumbers[index]==number){selectedNumbers.splice(index,1);found=true;}
index--;}
if(found){if(game.betProgression!=1){if(maxedOutCount==0){game.betProgression--;}else{maxedOutCount--;}}
game.updateFields();}
bet.bettingState="none";}
function addRowsToHTML(){var column0="<td id=\"0\" class=\"green zero\" rowspan=\"3\">0</td>";document.getElementById('rowThree').innerHTML=column0;document.getElementById('rowOne').innerHTML=convertRowToHTML(rowOne,"1st");document.getElementById('rowTwo').innerHTML=convertRowToHTML(rowTwo,"2nd");document.getElementById('rowThree').innerHTML+=convertRowToHTML(rowThree,"3rd");}
function drawSelectedNumbers(){var lastTwelve=[];if(selectedNumbers.length>12){var lastNumberIndex=selectedNumbers.length-12;for(i=0;i<12;i++){lastTwelve.push(selectedNumbers[lastNumberIndex]);lastNumberIndex++;}}else{lastTwelve=selectedNumbers;}
document.getElementById('selectedNumbers').innerHTML=createSelectedRowHTML(lastTwelve);if(selectedNumbers.length==0){document.getElementById('numbers-text').style.display="none";}else{document.getElementById('numbers-text').style.display="block";}}
function addOnclickEventsToTable(){var table=document.getElementById("numTable");if(table!==null){for(var i=0;i<table.rows.length-1;i++){for(var j=0;j<table.rows[i].cells.length-1;j++)
table.rows[i].cells[j].onclick=function(){numberClicked(this);};}}}
function betFails(number){if(number==0)return true;if(bet.bettingState==="row"){return(game.currentBetRow!==0&&game.currentBetRow!=bet.checkRow(number));}else if(bet.bettingState==="col"){return(game.current12Bet!==0&&game.current12Bet!=bet.check12(number));}else{return false;}}
function betSucceeds(number){if(number==0)return false;if(bet.bettingState==="row"){return(game.currentBetRow!==0&&game.currentBetRow==bet.checkRow(number));}else if(bet.bettingState==="col"){return(game.current12Bet!==0&&game.current12Bet==bet.check12(number));}else{return false;}}
function numberClicked(tableCell){selectedNumbers.push(tableCell.innerHTML);drawSelectedNumbers();if(bet.bettingState!="none"){if(betFails(tableCell.innerHTML)){if(game.betProgression>=8){showAlertBox("Maximum Bets Placed.","Quit Table!","This happens rarely and has happened to me too. <br /> Don't lose hope. Deposit a lower amount or same amount and go at it again - approximately after 24 hours.");maxedOutCount++;}else{game.failedBet();}}else{if(betSucceeds(tableCell.innerHTML)){bet.bettingState="none";game.winningBet();if(game.winAmount>=game.winningTarget){showAlertBox("Target Reached.","Quit Table!","You have successfully reached the recommended target.<br />You may continue playing approximately after 24 hours. Or, you may continue playing at another casino that I have personally tested and recommended.");}}}}
checkBetAndDisplay();}
function showAlertBox(heading,subheading,message){document.getElementById('modal-heading').innerHTML=heading;document.getElementById('modal-subheading').innerHTML=subheading;document.getElementById('modal-content-div').innerHTML=message;document.getElementById('myModal').style.display="block";}
function toggleChip(id){var element=document.getElementById(id);if(element.innerHTML===""){turnOffChip(id);}else{turnOnChip(id);}}
function turnOnChip(id){var element=document.getElementById(id);element.className="green bet";element.innerHTML="";chipID=id;}
function turnOffChip(id){var element=document.getElementById(id);element.className="green";element.innerHTML=id;}
function checkBetAndDisplay(){switch(bet.checkBet(selectedNumbers)){case 0:clearChipsOnBets();updateMessage('');game.currentBetRow=0;game.current12Bet=0;break;case 1:clearChipsOnBets();turnOnChip('1st');updateMessage('<b>$'+game.currentBet+'</b> on <b>1st Column</b>');game.currentBetRow=1;break;case 2:clearChipsOnBets();turnOnChip('2nd');updateMessage('<b>$'+game.currentBet+'</b> on <b>2nd Column</b>');game.currentBetRow=2;break;case 3:clearChipsOnBets();turnOnChip('3rd');updateMessage('<b>$'+game.currentBet+'</b> on <b>3rd Column</b>');game.currentBetRow=3;break;case 12:clearChipsOnBets();turnOnChip('1st 12');updateMessage('<b>$'+game.currentBet+'</b> on <b>1st 12</b>');game.current12Bet=12;break;case 24:clearChipsOnBets();turnOnChip('2nd 12');updateMessage('<b>$'+game.currentBet+'</b> on <b>2nd 12</b>');game.current12Bet=24;break;case 36:clearChipsOnBets();turnOnChip('3rd 12');updateMessage('<b>$'+game.currentBet+'</b> on <b>3rd 12</b>');game.current12Bet=36;break;default:clearChipsOnBets();updateMessage('');game.currentBetRow=0;game.current12Bet=0;}}
